function checkKeyInObj(key, obj) {
    if ( typeof obj !== "object" || obj === null ) {
        return false;
    }

    const keys = key.split('.')

    for ( const part of keys ) {
        const match = part.match(/^([^\[\]]+)(?:\[(\d+)\])?$/); // Matches "property" or "property[index]"
        if ( !match ) {
            return { exist: false, value: undefined };
        }

        const property = match[1] // The property name
        const index = match[2]   // The optional array index

        // Ensure `obj` is an object or array and has the property
        if ( typeof obj !== "object" || obj === null || !( property in obj ) ) {
            return { exist: false, value: undefined }
        }
        obj = obj[property] // Move to the next level

        // Handle array indexing
        if (index !== undefined) {
            if ( !Array.isArray(obj) || !( index in obj ) ) {
                return { exist: false, value: undefined }
            }
            obj = obj[index]; // Move to the indexed array element
        }
    }
    return { exist: true, value: obj }
}

function dateConvert(timestamp) {
    const date = new Date(timestamp)
    var day = date.getDate().toString()
    var month = (date.getMonth() + 1).toString()
    var year = date.getFullYear().toString()
    var hour = date.getHours().toString()
    var minutes = date.getMinutes().toString()
    var seconds = date.getSeconds().toString()

    day = checkZero(day)
    month = checkZero(month)
    year = checkZero(year)
    hour = checkZero(hour)
    minutes = checkZero(minutes)
    seconds = checkZero(seconds)

    return year + "/" + month + "/" + day + " " + hour + ":" + minutes + ":" + seconds + " GMT"
}

function checkZero(data) {
    if (data.length == 1) {
        data = "0" + data;
    }
    return data;
}

